# src/hypercode/core/ast.py
"""
Abstract Syntax Tree (AST) for HyperCode.
This module defines the node classes for the HyperCode AST.
"""

from dataclasses import dataclass
from typing import List, Optional, Any, Union


class Node:
    """Base class for all AST nodes."""

    pass


@dataclass
class Program(Node):
    """Represents a complete HyperCode program."""

    statements: List[Node]


@dataclass
class Function(Node):
    """Represents a function definition."""

    name: str
    params: List[str]
    body: List[Node]


@dataclass
class VariableDeclaration(Node):
    """Represents a variable declaration."""

    name: str
    initializer: Optional[Node] = None


@dataclass
class Literal(Node):
    """Represents a literal value (number, string, boolean, etc.)."""

    value: Any
    type: str  # 'number', 'string', 'boolean', 'null', etc.


@dataclass
class BinaryOp(Node):
    """Represents a binary operation (e.g., 1 + 2)."""

    left: Node
    operator: str
    right: Node


@dataclass
class UnaryOp(Node):
    """Represents a unary operation (e.g., -1 or !true)."""

    operator: str
    right: Node


@dataclass
class Variable(Node):
    """Represents a variable reference."""

    name: str


@dataclass
class Call(Node):
    """Represents a function call."""

    callee: Node
    arguments: List[Node]


@dataclass
class Return(Node):
    """Represents a return statement."""

    value: Optional[Node] = None


@dataclass
class Block(Node):
    """Represents a block of statements."""

    statements: List[Node]


@dataclass
class If(Node):
    """Represents an if statement."""

    condition: Node
    then_branch: Node
    else_branch: Optional[Node] = None


@dataclass
class While(Node):
    """Represents a while loop."""

    condition: Node
    body: Node


@dataclass
class For(Node):
    """Represents a for loop."""

    initializer: Optional[Node]
    condition: Optional[Node]
    increment: Optional[Node]
    body: Node


@dataclass
class Assign(Node):
    """Represents a variable assignment."""

    target: Node
    value: Node


@dataclass
class Logical(Node):
    """Represents a logical operation (and/or)."""

    left: Node
    operator: str
    right: Node
